import { createContext, useEffect, useContext, useState } from "react";
import axios from "axios";
import Avatar from "../assets/images/avatar.png";

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  // localStorage.removeItem("token");

  const token = localStorage.getItem("token");

  const authHeader = () => {
    const token = JSON.parse(localStorage.getItem("token"));
    return { Authorization: "Bearer " + token };
  };

  const getInfoUser = async () => {
    axios
      .get("http://localhost:8000/api/v1/users/information", {
        headers: authHeader(),
      })
      .then((res) => {
        let data = [];
        // data.id = res.data.manguoidung;
        // data.username = res.data.username;
        // data.email = res.data.email;
        // data.image = Avatar;
        // data.fullname = res.data.fullname;
        // data.gender = res.data.gender;
        // data.dateOfBirth = res.data.dateofbirth;
        // data.phone = res.data.phonenumber;
        // data.address = res.data.address;
        localStorage.setItem('user', res.data);
      });
  };

  if (token != null) {
    getInfoUser();
  }

  const data = localStorage.getItem('user');
  console.log(data.id);

  const [user] = useState({
    id : data.id,
    username : data.username,
    email : data.email,
    image : data.image,
    fullname : data.fullname,
    gender : data.gender,
    dateOfBirth : data.dateOfBirth,
    phone : data.phone,
    address : data.address,
  });

  localStorage.removeItem('user');

  return <AuthContext.Provider value={user}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  return context;
};
