import { createSlice } from '@reduxjs/toolkit'
import { STATUS } from '../../utils/constants'
import Thumbnail from '../../assets/images/thumbnail.png';

var fakeData = [
    {
        id: '120213991',
        name: 'Sự kiện số 1',
        dateStart: '1/1/2023',
        dateEnd: '1/1/2023',
        status: STATUS.ACTIVE,
        note: 'something',
        thumbnail: Thumbnail
    },
]

const getAllEvent = async () => {
  await axios
    .get(
      "http://localhost:8000/api/v1/event/all_event",
      {
        headers: {
          Authorization: "Bearer " + localStorage.getItem("token"),
        },
      }
    ).then((res) => {
      console.log(res);
    });
}
getAllEvent();
const initialState = {
  evens: fakeData
}

export const eventSlices = createSlice({
  name: 'event',
  initialState,
  reducers: {
    listEvent: (state) => {
      return state.evens
    },
    filterEvent: (state, actions) =>{
        if(actions.payload !== STATUS.ALL) {
            return state.evens.filter((item) => item.status === actions.payload)
        }
            return state.evens
    }
  },
})

// Action creators are generated for each case reducer function
export const { listEvent } = eventSlices.actions

export default eventSlices.reducer
