import React, { useState, useContext } from "react";
import axios from "axios";
import "./modalDetailEvent.css";
import "./modalLogin.css";
import Button from "../ui/Button";
import TextField from "../ui/TextField";

const FormLogin = (setStatus) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const authHeader = (token) => {
    if (token == null) return {};
    return { Authorization: "Bearer " + token };
  };

  const getInfoUser = async (token) => {
    await axios
      .get("http://localhost:8000/api/v1/users/information", {
        headers: authHeader(token),
      })
      .then((res) => {
        localStorage.setItem("id", res.data.manguoidung);
        localStorage.setItem("username", res.data.username);
        localStorage.setItem("email", res.data.email);
        localStorage.setItem("fullname", res.data.fullname);
        localStorage.setItem("gender", res.data.gender);
        localStorage.setItem("dateOfBirth", res.data.dateofbirth);
        localStorage.setItem("phone", res.data.phonenumber);
        localStorage.setItem("address", res.data.address);
      });
  };

  const submitLogin = async () => {
    await axios
      .post(
        "http://localhost:8000/api/v1/auth/login",
        JSON.stringify(
          `grant_type=&username=${username}&password=${password}&scope=&client_id=&client_secret=`
        )
      )
      .then((response) => {
        localStorage.setItem("token", response.data.access_token);
        getInfoUser(response.data.access_token);
        window.location.reload();
      })
  };

  const handleSubmit = async () => {
    await submitLogin();
  };

  return (
    <div className="modal-content">
      <h1>Đăng nhập</h1>
      <div className="form-login">
        <TextField
          title={"Username"}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <TextField
          title={"Password"}
          value={password}
          type="password"
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <div className="form-action">
        <Button
          title={"Đăng nhập"}
          type={"button"}
          styles={{
            backgroundColor: "#F1E040",
            color: "#000",
            borderRadius: "10px",
            width: "120px",
          }}
          onClick={() => handleSubmit()}
        />
        <div className="flex">
          Chưa có tài khoản?{" "}
          <Button
            title={"Đăng ký "}
            type={"link"}
            styles={{}}
            onClick={() => setStatus("register")}
          />
        </div>
      </div>
    </div>
  );
};

const FormRegister = (setStatus) => {
  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleOnChange = (key, value) => {
    setForm({
      ...form,
      [key]: value,
    });
  };
  return (
    <div className="modal-content">
      <h1>Đăng ký</h1>
      <div className="form-login">
        <TextField
          title={"Tên đăng nhập "}
          value={form.email}
          onChange={(e) => handleOnChange("email", e.target.value)}
        />
        <TextField
          title={"Email"}
          value={form.Email}
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
        <TextField
          title={"Số điện thoại"}
          value={form.password}
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
        <TextField
          title={"Địa chỉ"}
          value={form.password}
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
        <TextField
          title={"Giới tính"}
          value={form.password}
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
        <TextField
          title={"Mật khẩu"}
          value={form.password}
          type="password"
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
        <TextField
          title={"Xác nhận mật khẩu"}
          value={form.password}
          type="password"
          onChange={(e) => handleOnChange("password", e.target.value)}
        />
      </div>
      <div className="form-action">
        <Button
          title={"Save"}
          type={"button"}
          styles={{
            backgroundColor: "#F1E040",
            color: "#000",
            borderRadius: "10px",
            width: "100px",
          }}
        />
        <div className="flex">
          <Button
            title={"Đăng nhập"}
            type={"link"}
            styles={{}}
            onClick={() => setStatus("login")}
          />
        </div>
      </div>
    </div>
  );
};

const ModalLogin = () => {
  const [status, setStatus] = useState("login");

  const renderForm = () => {
    switch (status) {
      case "login":
        return FormLogin(setStatus);
      case "register":
        return FormRegister(setStatus);
      default:
        return FormLogin(setStatus);
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal modal-login">{renderForm()}</div>
    </div>
  );
};

export default ModalLogin;
