import React from "react";
import "./modalDetailEvent.css";
import TextField from "../ui/TextField";
import Button from "../ui/Button";

function ModalDetailEvent({ isOpen, onClose, ...other }) {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="modal-close" onClick={onClose}>
          &times;
        </button>
        <div className="modal-content">
          <div className="name-event">
            <h1>Sự kiện số 1</h1>
          </div>
          <div className="card-modal">
            <div className="card-modal__item">
              <img className="card-modal__img" alt="" src={other?.thumbnail} />
              <div className="card-detail card-address">
                <TextField
                  title={"Ghi chú"}
                  value={other?.note}
                  onChange={() => {}}
                />
              </div>
            </div>
            <div
              className="card-detail card-modal__item flex"
              style={{ gap: "40px", alignItems: "flex-start" }}
            >
              <div className="event-item">
                <TextField
                  title={"Chủ sở hữu"}
                  value={other?.owner}
                  onChange={() => {}}
                />
                <TextField
                  title={"Mã sự kiện"}
                  value={other?.id}
                  onChange={() => {}}
                />
                <TextField
                  title={"Ngày bắt đầu"}
                  value={other?.dateStart}
                  onChange={() => {}}
                />
                <TextField
                  title={"Ngày kết thúc"}
                  value={other?.dateEnd}
                  onChange={() => {}}
                />
              </div>
            </div>
          </div>
          <div id="formUpdateEvent">
            <h4 class="error-message" id="errmsg">
              Sai thông tin!
            </h4>
          </div>
          <div className="list-button flex">
            <Button
              title={"Cancel"}
              styles={{
                backgroundColor: "red",
                color: "#fff",
              }}
              type={"button"}
              onClick={onClose}
            />
            <Button
              title={"Save"}
              type={"button"}
              styles={{
                backgroundColor: "#409FC8",
                color: "#fff",
              }}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default ModalDetailEvent;
