import React, { useState } from "react";
import "./modalDetailEvent.css";
import TextField from "../ui/TextField";
import Button from "../ui/Button";
import axios from "axios";

localStorage.setItem("changepassSuccess", true);

const removeErrMsg = () => {
  document.querySelectorAll("#errmsg")?.forEach((e) => e.remove());
};

function ModalCreateEvent({ isOpen, onClose, ...other }) {
  const [form, setForm] = useState({
    passold: "",
    passnew: "",
    cfpassnew: "",
  });

  const handleOnChange = (key, value) => {
    removeErrMsg();
    setForm({
      ...form,
      [key]: value,
    });
  };

  const handleChangePass = async () => {
    // if (form.cfpassnew !== form.passnew) {
    //   localStorage.setItem("changepassSuccess", false);
    // } else {
    //   try {
    //     await axios
    //       .put(
    //         "http://localhost:8000/api/v1/users/change_password/",
    //         {
    //           password: form.passold,
    //           new_password: form.passnew,
    //         },
    //         {
    //           headers: {
    //             Authorization: "Bearer " + localStorage.getItem("token"),
    //           },
    //         }
    //       )
    //       .then((res) => {
    //         if (res?.statusText === "OK") {
    //           localStorage.setItem("changepassSuccess", true);
    //           window.location.reload();
    //           localStorage.removeItem("changepassSuccess");
    //         } else {
    //           localStorage.setItem("changepassSuccess", false);
    //         }
    //       });
    //   } catch (error) {
    //     localStorage.setItem("changepassSuccess", false);
    //   }
    // }
  };

  return (
    <div className="modal-overlay">
      <div className="modal">
        <button className="modal-close" onClick={onClose}>
          &times;
        </button>
        <div className="modal-content" styles={{backgroundColor: 'white'}}>
          <h1>Tạo sự kiện</h1>
          <div
            className="card-detail card-modal__item flex"
            style={{ gap: "40px", alignItems: "flex-start" }}
          >
            <div className="event-item">
              <TextField title={"Chủ sở hữu"} value={"a"} onChange={() => {}} />
              <TextField
                title={"Ngày bắt đầu"}
                value={"a"}
                onChange={() => {}}
              />
              <TextField
                title={"Số người tham dự"}
                value={"a"}
                onChange={() => {}}
              />
              <TextField
                title={"Phí phạt (VND)"}
                value={"a"}
                onChange={() => {}}
              />
              <TextField
                title={"Số dư nợ (VND)"}
                value={"a"}
                onChange={() => {}}
              />
            </div>
            <div className="event-item">
              <TextField title={"Mã sự kiện"} value={"a"} onChange={() => {}} />
              <TextField
                title={"Ngày kết thúc"}
                value={"a"}
                onChange={() => {}}
              />
              <TextField
                title={"Tổng chi phí (VND)"}
                value={"a"}
                onChange={() => {}}
              />
              <TextField
                title={"Đã thanh toán (VND)"}
                value={"a"}
                onChange={() => {}}
              />
            </div>
          </div>
          <div className="form-action-change-pass">
            <Button
              title={"Tạo"}
              type={"button"}
              styles={{
                backgroundColor: "#409FC8",
                color: "#fff",
                borderRadius: "10px",
              }}
              onClick={async () => await handleChangePass()}
            />
            <Button
              title={"Hủy"}
              styles={{
                backgroundColor: "transparent",
                color: "#000",
                border: "1px solid",
                borderRadius: "10px",
              }}
              type={"button"}
              onClick={onClose}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default ModalCreateEvent;
