import { createContext, useEffect, useContext, useState } from "react";
import axios from "axios";
import Avatar from "../assets/images/avatar.png";

export const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
  // localStorage.removeItem("token");

  const token = localStorage.getItem("token");

  const authHeader = () => {
    const token = JSON.parse(localStorage.getItem("token"));
    return { Authorization: "Bearer " + token };
  };

  let data = {};
  const getInfoUser = async () => {
    axios
      .get("http://localhost:8000/api/v1/users/information", {
        headers: authHeader(),
      })
      .then((res) => {
        data.push(res.data.manguoidung);
        data.push(res.data.username);
        data.push(res.data.email);
        data.push(Avatar);
        data.push(res.data.fullname);
        data.push(res.data.gender);
        data.push(res.data.dateofbirth);
        data.push(res.data.phonenumber);
        data.push(res.data.address);
      });
  };

  if (token != null) {
    getInfoUser();
  }

  console.log(data);

  const [user] = useState({
    id : data[0],
    username : data[1],
    email : data[2],
    image : data[3],
    fullname : data[4],
    gender : data[5],
    dateOfBirth : data[6],
    phone : data[7],
    address : data[8],
  });

  return <AuthContext.Provider value={user}>{children}</AuthContext.Provider>;
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  return context;
};
