import { createContext, PropsWithChildren, useContext, useState } from 'react';
import Avatar from "../assets/images/avatar.png"

export const AuthContext = createContext();

// export default function AuthProvider({
//   children, userLogin = null
// }) {
//   const [user] = useState(userLogin ? { id: null, userName: null, password: null, image: Avatar } : null);

//   return <AuthContext.Provider value={user}>{children}</AuthContext.Provider>;
// }

export default function AuthProvider (props){
  const [token, setToken] = useState(localStorage.getItem("awesomeLeadsToken"));

  useEffect(() => {
    const fetchUser = async () => {
      const requestOptions = {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
      };

      const response = await fetch("/api/users/me", requestOptions);

      if (!response.ok) {
        setToken(null);
      }
      localStorage.setItem("awesomeLeadsToken", token);
    };
    fetchUser();
  }, [token]);

  return (
    <AuthProvider.Provider value={[token, setToken]}>
      {props.children}
    </AuthProvider.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  return context;
};
