import { createContext, useEffect, useContext, useState } from 'react';
import Avatar from "../assets/images/avatar.png"
const AuthContext = createContext(null);

export default function AuthProvider({
  children
}) {
  const [token, setToken] = useState(localStorage.getItem("awesomeLeadsToken"));

  useEffect(() => {
    const fetchUser = async () => {
      const requestOptions = {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
      };

      const response = await fetch("/api/users/me", requestOptions);

      if (!response.ok) {
        setToken(null);
      }
      localStorage.setItem("awesomeLeadsToken", token);
    };
    fetchUser();
  }, [token]);

  const [user] = useState(userLogin ? { id: null, userName: null, password: null, image: Avatar } : null);

  return <AuthContext.Provider value={user}>{children}</AuthContext.Provider>;
}

export const useAuth = () => {
  const context = useContext(AuthContext);

  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }

  return context;
};

export const useAuthLogin = () => {
  const setAuthData = useContext(AuthContext);
  setAuthData(() => [{
    id: 1, userName: 2, password: 3, image: Avatar
  }]);
}
